(1691, 'MTB109', 'Máy tính bảng Cink ER8416- RAM 1,5GB - ER8416', 99, 'May-tinh-bang-Cink-ER8416--RAM-1,5GB---ER8416', '', '1', '', '<div class="cont-tab-detail_ cont-tab-tech-prod_">\n<div id="tech-inf" class="content_tab tech-info ">\n<div class="qa">\n<div class="product_options product-options-tech">\n<div class="attribute-origin-tech tskt for-desc">\n<div class="attrs"><label class="label">M&agrave;u sắc:</label>\n<div class="attrs-item ">Đen</div>\n</div>\n<div class="attrs"><label class="label">T&iacute;nh năng nổi trội:</label>\n<div class="attrs-item ">M&agrave;n h&igrave;nh cảm ứng,2 SIM,3G,C&oacute; khe cắm thẻ nhớ,MP3,Wi-Fi</div>\n</div>\n<div class="attrs"><label class="label">Dung lượng pin:</label>\n<div class="attrs-item ">3650mAh</div>\n</div>\n<div class="attrs"><label class="label">Hệ điều h&agrave;nh:</label>\n<div class="attrs-item ">Android</div>\n</div>\n<div class="attrs"><label class="label">Loại l&otilde;i chip:</label>\n<div class="attrs-item ">Bốn nh&acirc;n - Quad Core</div>\n</div>\n<div class="attrs"><label class="label">Bộ nhớ trong:</label>\n<div class="attrs-item ">8GB</div>\n</div>\n<div class="attrs"><label class="label">H&atilde;ng sản xuất:</label>\n<div class="attrs-item ">Cink</div>\n</div>\n<div class="attrs"><label class="label">K&iacute;ch thước m&agrave;n h&igrave;nh:</label>\n<div class="attrs-item ">Từ 7\'\' - 8\'\'</div>\n</div>\n<div class="attrs"><label class="label">RAM:</label>\n<div class="attrs-item ">1GB</div>\n</div>\n<div class="attrs"><label class="label">T&igrave;nh trạng m&aacute;y:</label>\n<div class="attrs-item ">Mới</div>\n</div>\n<div class="attrs"><label class="label">Xuất xứ:</label>\n<div class="attrs-item ">H&agrave;ng ch&iacute;nh h&atilde;ng</div>\n</div>\n<div class="attrs"><label class="label">Bảo h&agrave;nh:</label>\n<div class="attrs-item ">12 th&aacute;ng ch&iacute;nh h&atilde;ng</div>\n</div>\n<div class="attrs"><label class="label">Camera:</label>\n<div class="attrs-item ">2MP/0.3MP</div>\